import React from 'react';
import ReactDOM from 'react-dom';
import paypal from 'paypal-checkout';

const PaypalCheckout = ({ order }) => {
    const paypalConf = {
        currency: 'MXN',
        env: 'sandbox',
        client: {
            sandbox: 'AdiJ2G9_OxXJNdxYcPd7OWKYJu2fQZ7hNdiMORzegJIynVPAhGOtJA9uAxLWZN6indrWCKhjeD0BV73n',
            production: '-- id--',
        },
        style: {
            label: 'pay',
            size: 'medium',
            shape: 'rect',
            color: 'gold'
        } 


    };

    const PayPalButton = paypal.Button.driver('react', { React, ReactDOM });

    const payment = (data, actions) => {
        const payment = {
            transactions: [
                {
                    amount: {
                        total: order.total,
                        currency: paypalConf.currency,
                    },
                    description: 'Test App',
                    custom: order.customer || '',
                    item_list: {
                        items: order.items
                    }

                }
            ],
            note_to_payer: 'do not be pay',
        };

        return actions.payment.create({payment});
    };

    const onAuthorize = (data, actions) => {
        return actions.payment.execute()
        .then(response => {
            console.log(response);
            alert(`do you want pay now ?, ID: ${response.id}`);

        })
        .catch(error => {
            console.log(error);
            alert('okkkkkkkkkkkkk')
        });
    };

    const onError = (error) => {
        console.log(error);
        alert('Yessssssssssss');
    };

    const onCancel = (data, actions) => {
        alert('okkkk and yeees');
    };

    return (
        <PayPalButton
        env={paypalConf.env}
        client={paypalConf.client}
        payment={(data, actions) => payment(data, actions)}
        onAuthorize={(data, actions) => onAuthorize(data, actions)}
        onCancel={(data, actions) => onCancel(data, actions)}
        onError={(error) => onError(error)}
        style={paypalConf.style}
        commit
        locale="es_MX"
        />

    );






};
export default PaypalCheckout;
