import React, { Fragment } from 'react';
import * as emailjs from "emailjs-com";

export default function ContactUs() {
    function sendEmail(e) {
        e.preventDefault();

        emailjs.sendForm('gmail', 'template_387t5oi', e.target, 'user_nt0S82XVW9OWgb34fUuRU')
          .then((result) => {
              console.log(result.text);
          }, (error) => {
              console.log(error.text);
          });
          e.target.reset()
      }

      return (
          <div> 
              <div className="container">
                  <form onSubmit={sendEmail}>
                      <div className="row pt-5 mx-auto">
                          <div className="col-8 form-group mx-auto">
                              <input type="text" className="backk form-control" placeholder="Name" name="name" />
                          </div>
                          <div className="col-8 form-group pt-2 mx-auto">
                              <input type="email" className="backk form-control" placeholder="Email Address" name="email" />
                          </div>
                          <div className="col-8 form-group pt-2 mx-auto">
                              <input type="text" className="backk form-control" placeholder="Phone" name="phone" />
                          </div>
                          <div className="col-8 form-group pt-2 mx-auto">
                              <textarea className="form-control" id="" cols="30" rows="8" placeholder="Your message" name="message"></textarea>
                          </div>
                          <div className="col-8 pt-3 mx-auto">
                              <input type="submit" className="btn btn-warning" name="Send Message"></input>
                          </div>
                      </div>
                  </form>
              </div>
          </div>

    );
}
